mapper.insert:
insert into company (
            company_no
          , company_name
          , owner_name
          , business_no
          , resident_no
          , business_place
          , telephone
          , reg_date
          , reg_company
          , reg_member
)
values (
            (select isnull(max(company_no), 0) + 1
               from company) 
          , #{companyName}
          , #{ownerName}
          , #{businessNo}
          , #{residentNo} 
          , #{businessPlace}
          , #{telephone}
          , convert(varchar(20), getdate(), 20)
          , #{regCompany}
          , #{regMember}
);

mapper.update:
update company
   set company_name = #{companyName}
     , owner_name = #{ownerName}
     , business_no = #{businessNo}
     , resident_no = #{residentNo}
     , business_place = #{businessPlace}
     , telephone = #{telephone}
     , upt_date = convert(varchar(20), getdate(), 20)
     , upt_company = #{uptCompany}
     , upt_member = #{uptMember}
 where company_no = #{companyNo};
