mapper.insert:
insert into company (
            company_name
          , owner_name
          , business_no
          , resident_no
          , company_div
          , business_place
          , nation
          , telephone
          , regist_date
          , regist_user
)
values (
            #{companyName}
          , #{ownerName}
          , #{businessNo}
          , #{residentNo}
          , #{companyDiv} 
          , #{postCode}
          , #{businessPlace}
          , #{nation}
          , #{telephone}
          , convert(varchar(20), getdate(), 20)
          , #{registUser}
);

mapper.update:
update company
   set company_name = #{companyName}
     , owner_name = #{ownerName}
     , business_no = #{businessNo}
     , resident_no = #{residentNo}
     , company_div = #{companyDiv}
     , business_place = #{businessPlace}
     , nation = #{nation}
     , telephone = #{telephone}
     , modify_date = convert(varchar(20), getdate(), 20)
     , modify_user = #{modifyUser}
 where company_no = #{companyNo};
