mapper.insert:
insert into company (
            company_no
          , company_name
          , owner_name
          , business_no
          , resident_no
          , business_place
          , telephone
          , regist_datetime
          , regist_company
          , regist_user
)
values (
            (select isnull(max(company_no), 0) + 1
               from company) 
          , #{companyName}
          , #{ownerName}
          , #{businessNo}
          , #{residentNo} 
          , #{businessPlace}
          , #{telephone}
          , convert(varchar(20), getdate(), 20)
          , #{regCompany}
          , #{regMember}
);

mapper.update:
update company
   set company_name = #{companyName}
     , owner_name = #{ownerName}
     , business_no = #{businessNo}
     , resident_no = #{residentNo}
     , business_place = #{businessPlace}
     , telephone = #{telephone}
     , modify_datetime = convert(varchar(20), getdate(), 20)
     , modify_company = #{uptCompany}
     , modify_user = #{uptMember}
 where company_no = #{companyNo};
