mapper.select:
select max(contract_no) max_contract_no
  from contract
 where company_no = #{companyNo}
   and contract_date = #{contractDate};

mapper.insert:
insert into contract (
            company_no
          , contract_no
          , contract_name
          , contract_date
          , contractor
          , contract_service
          , contract_div
          , contract_period
          , start_date
          , end_date
          , monetary_unit
          , exchange_rate
          , contract_manager
          , remark
          , regist_date
          , regist_user
)
values (
            #{companyNo}
          , (select #{contractDiv} + '-' + #{contractService} + '-' + convert(varchar(8), convert(datetime, #{contractDate}), 112) + '-' + format(isnull(right(max(contract_no), 3), 0) + 1, '000') 
               from contract
              where contract_div = #{contractDiv}
                and contract_service = #{contractService}
                and contract_date = #{contractDate})
          , #{contractName}
          , #{contractDate}
          , #{contractor}
          , #{contractService}
          , #{contractDiv}
          , #{contractPeriod}
          , #{startDate}
          , #{endDate}
          , #{monetaryUnit}
          , #{exchangeRate}
          , #{contractManager}
          , #{remark}
          , convert(varchar(20), getdate(), 20)
          , #{registUser}
);

mapper.update:
update contract
   set contract_name = #{contractName}
     , contract_date = #{contractDate}
     , contractor = #{contractor}
     , contract_service = #{contractService}
     , contract_div = #{contractDiv}
     , contract_period = #{contractPeriod}
     , start_date = #{startDate}
     , end_date = #{endDate}
     , monetary_unit = #{monetaryUnit}
     , exchange_rate = #{exchangeRate}
     , remark = #{remark}
     , contract_manager = #{contractManager}
     , modify_date = convert(varchar(20), getdate(), 20)
     , modify_user = #{modifyUser}
 where contract_no = #{contractNo};

mapper.insert:
insert into license (
            company_no
          , contract_no
          , license_no
          , license_type
          , license_div
          , license_count
          , start_date
          , end_date
          , unit_price
          , amount
          , discounted_amount
          , actual_amount
          , regist_datetime
          , regist_company
          , regist_user
)
values (
            #{companyNo}
          , #{contractNo}
          , (select #{licenseType} + #{contractService} + '-' + convert(varchar(4), convert(datetime, #{contractDate}), 112) + '-' + right(convert(varchar(8), convert(datetime, #{contractDate}), 112), 4) + '-' + #{licenseDiv} + format(isnull(right(max(license_no), 3), 0) + 1, '000')
               from license
              where contract_no = #{contractNo}
                and license_type = #{licenseType})
          , #{licenseType}
          , #{licenseDiv}
          , #{licenseCount}
          , #{startDate}
          , #{endDate}
          , #{unitPrice}
          , #{amount}
          , #{discountedAmount}
          , #{actualAmount}
          , convert(varchar(20), getdate(), 20)
          , #{registCompany}
          , #{registUser}
);

mapper.update:
update license
   set license_count = #{licenseCount}
     , start_date = #{startDate}
     , end_date = #{endDate}
     , unit_price = #{unitPrice}
     , amount = #{amount}
     , discounted_amount = #{discountedAmount}
     , actual_amount = #{actualAmount}
     , modify_datetime = convert(varchar(20), getdate(), 20)
     , modify_company = #{modifyCompany}
     , modify_user = #{modifyUser}
 where company_no = #{companyNo}
   and contract_no = #{contractNo}
   and license_no = #{licenseNo};

mapper.delete:
delete from license
 where company_no = #{companyNo}
   and contract_no = #{contractNo}
   and license_no = #{licenseNo};

mapper.insert:
insert into termination (
            company_no
          , contract_no
          , termination_no
          , termination_date
          , termination_type
          , termination_reasons
          , cancellation_charge
          , cancellation_refund
          , termination_status
          , regist_datetime
          , regist_company
          , regist_user
)
values (
            #{companyNo}
          , #{contractNo}
          , (select #{contractNo} + format(isnull(substring(max(termination_no), 10, 2), 0) + 1, '00')
               from termination
              where company_no = #{companyNo}
                and contract_no = #{contractNo})
          , #{terminationDate}
          , #{terminationType}
          , #{terminationReasons}
          , 0
          , 0
          , '1'
          , convert(varchar(20), getdate(), 20)
          , #{registCompany}
          , #{registUser}
);

mapper.update:
update contract
   set cancellation_charge = ${cancellationCharge}
     , cancellation_refund = ${cancellationRefund}
     , termination_status = ${terminationStatus}
     , modify_datetime = convert(varchar(20), getdate(), 20)
     , modify_company = #{modifyCompany}
     , modify_user = #{modifyUser}
 where company_no = #{companyNo}
   and contract_no = #{contractNo}
   and termination_no = #{terminationNo};

update contract
   set termination_status = 'C'
     , cancellation_date = convert(varchar(20), getdate(), 20)
     , modify_datetime = convert(varchar(20), getdate(), 20)
     , modify_company = #{modifyCompany}
     , modify_user = #{modifyUser}
 where company_no = #{companyNo}
   and contract_no = #{contractNo}
   and termination_no = #{terminationNo};
