mapper.select:
select max(contract_no) max_contract_no
  from contract
 where company_no = #{companyNo}
   and contract_date = #{contractDate};

mapper.insert:
insert into contract (
            company_no
          , contract_no
          , contract_name
          , contract_date
          , contractor
          , contract_period
          , start_date
          , end_date
          , monetary_unit
          , exchange_rate
          , amount
          , discounted_amount
          , actual_amount
          , remark
          , regist_datetime
          , regist_company
          , regist_user
)
values (
            #{companyNo}
          , (select 'C' + convert(varchar(8), convert(datetime, #{contractDate}), 12) + format(isnull(max(company_no), 0) + 1, '000') 
               from contract
              where company_no = #{companyNo}
                and contract_date = #{contractDate})
          , #{contractName}
          , #{contractDate}
          , #{contractor}
          , #{contractPeriod}
          , #{startDate}
          , #{endDate}
          , #{monetaryUnit}
          , #{exchangeRate}
          , #{amount}
          , #{discountedAmount}
          , #{actualAmount}
          , #{remark}
          , convert(varchar(20), getdate(), 20)
          , #{registCompany}
          , #{registUser}
);

mapper.update:
update contract
   set contract_name = #{contractName}
     , contract_date = #{contractDate}
     , contractor = #{contractor}
     , contract_period = #{contractPeriod}
     , start_date = #{startDate}
     , end_date = #{endDate}
     , monetary_unit = #{monetaryUnit}
     , exchange_rate = #{exchangeRate}
     , remark = #{remark}
     , amount = #{amount}
     , discounted_amount = #{discountedAmount}
     , actual_amount = #{actualAmount}
     , modify_datetime = convert(varchar(20), getdate(), 20)
     , modify_company = #{modifyCompany}
     , modify_user = #{modifyUser}
 where company_no = #{companyNo}
   and contract_no = #{contractNo};

mapper.delete:
delete from contract
 where company_no = #{companyNo}
   and contract_no = #{contractNo};

mapper.insert:
insert into license (
            company_no
          , contract_no
          , license_no
          , license_type
          , app_name
          , license_count
          , start_date
          , end_date
          , unit_price
          , amount
          , discounted_amount
          , actual_amount
          , regist_datetime
          , regist_company
          , regist_user
)
values (
            #{companyNo}
          , #{contractNo}
          , (select #{licenseType} + format(isnull(substring(max(license_no), 3, 2), 0) + 1, '00') + #{appName}
               from license
              where company_no = #{companyNo}
                and contract_no = #{contractNo}
                and license_type = #{licenseType})
          , #{licenseType}
          , #{appName}
          , #{licenseCount}
          , #{startDate}
          , #{endDate}
          , #{unitPrice}
          , #{amount}
          , #{discountedAmount}
          , #{actualAmount}
          , convert(varchar(20), getdate(), 20)
          , #{registCompany}
          , #{registUser}
);

mapper.update:
update license
   set license_count = #{licenseCount}
     , start_date = #{startDate}
     , end_date = #{endDate}
     , unit_price = #{unitPrice}
     , amount = #{amount}
     , discounted_amount = #{discountedAmount}
     , actual_amount = #{actualAmount}
     , modify_datetime = convert(varchar(20), getdate(), 20)
     , modify_company = #{modifyCompany}
     , modify_user = #{modifyUser}
 where company_no = #{companyNo}
   and contract_no = #{contractNo}
   and license_no = #{licenseNo};

mapper.delete:
delete from license
 where company_no = #{companyNo}
   and contract_no = #{contractNo}
   and license_no = #{licenseNo};
