mapper.select:
select max(contract_no) max_contract_no
  from contract
 where company_no = #{companyNo}
   and contract_date = #{contractDate};

mapper.insert:
insert into contract (
            company_no
          , contract_no
          , contract_name
          , contract_date
          , contractor
          , contract_service
          , contract_div
          , contract_period
          , start_date
          , end_date
          , monetary_unit
          , exchange_rate
          , contract_manager
          , remark
          , regist_date
          , regist_user
)
values (
            #{companyNo}
          , (select #{contractDiv} + '-' + #{contractService} + '-' + convert(varchar(8), convert(datetime, #{contractDate}), 112) + '-' + format(isnull(right(max(contract_no), 3), 0) + 1, '000') 
               from contract
              where contract_div = #{contractDiv}
                and contract_service = #{contractService}
                and contract_date = #{contractDate})
          , #{contractName}
          , #{contractDate}
          , #{contractor}
          , #{contractService}
          , #{contractDiv}
          , #{contractPeriod}
          , #{startDate}
          , #{endDate}
          , #{monetaryUnit}
          , #{exchangeRate}
          , #{contractManager}
          , #{remark}
          , convert(varchar(10), getdate(), 121)
          , #{registUser}
);

mapper.update:
update contract
   set contract_name = #{contractName}
     , contract_date = #{contractDate}
     , contractor = #{contractor}
     , contract_service = #{contractService}
     , contract_div = #{contractDiv}
     , contract_period = #{contractPeriod}
     , start_date = #{startDate}
     , end_date = #{endDate}
     , monetary_unit = #{monetaryUnit}
     , exchange_rate = #{exchangeRate}
     , remark = #{remark}
     , contract_manager = #{contractManager}
     , modify_date = convert(varchar(10), getdate(), 121)
     , modify_user = #{modifyUser}
 where contract_no = #{contractNo};

mapper.insert:
insert into license (
            contract_no
          , license_no
          , license_type
          , license_div
          , license_count
          , start_date
          , end_date
          , unit_price
          , amount
          , discounted_amount
          , actual_amount
          , regist_date
          , regist_user
)
values (
            #{contractNo}
          , (select #{licenseType} + #{contractService} + '-' + convert(varchar(4), convert(datetime, #{contractDate}), 112) + '-' + right(convert(varchar(8), convert(datetime, #{contractDate}), 112), 4) + '-' + #{licenseDiv} + format(isnull(right(max(license_no), 3), 0) + 1, '000')
               from license
              where contract_no = #{contractNo}
                and license_type = #{licenseType})
          , #{licenseType}
          , #{licenseDiv}
          , #{licenseCount}
          , #{startDate}
          , #{endDate}
          , #{unitPrice}
          , #{amount}
          , #{discountedAmount}
          , #{actualAmount}
          , convert(varchar(10), getdate(), 121)
          , #{registUser}
);

mapper.update:
update license
   set license_count = #{licenseCount}
     , start_date = #{startDate}
     , end_date = #{endDate}
     , unit_price = #{unitPrice}
     , amount = #{amount}
     , discounted_amount = #{discountedAmount}
     , actual_amount = #{actualAmount}
     , modify_date = convert(varchar(10), getdate(), 121)
     , modify_user = #{modifyUser}
 where contract_no = #{contractNo}
   and license_no = #{licenseNo};

mapper.delete:
delete from license
 where contract_no = #{contractNo}
   and license_no = #{licenseNo};

mapper.insert:
insert into odm_contract (
            contract_no
          , odm_seq
          , odm_cont_div
          , pay_currency
          , pay_unit
          , pay_term_mo
          , unit_price
          , apply_date
          , regist_date
          , regist_user
)
values (
            #{contractNo}
          , (select isnull(max(odm_seq), 0) + 1
               from odm_contract
              where contract_no = #{contractNo})
          , #{odmContDiv}
          , #{payCurrency}
          , #{payUnit}
          , #{payTermMo}
          , #{unitPrice}
          , #{applyDate}
          , convert(varchar(10), getdate(), 121)
          , #{registUser}
);

mapper.update:
update odm_contract
   set odm_cont_div = #{odmContDiv}
     , pay_currency = #{payCurrency}
     , pay_unit = #{payUnit}
     , pay_term_mo = #{payTermMo}
     , unit_price = #{unitPrice}
     , apply_date = #{applyDate}
     , modify_date = convert(varchar(10), getdate(), 121)
     , modify_user = #{modifyUser}
 where contract_no = #{contractNo}
   and odm_seq = #{odmSeq};

mapper.delete:
delete from odm_contract
 where contract_no = #{contractNo}
   and odm_seq = #{odmSeq};

mapper.select:
select max(ship_seq) max_ship_seq
  from ships_contract
 where contract_no = #{contractNo};

mapper.insert:
insert into ships_contract (
            contract_no
          , ship_seq
          , ship_name
          , imo_no
          , equip_lent
          , apply_start_date
          , apply_finish_date
          , cancel_yn
          , regist_date
          , regist_user
)
values (
            #{contractNo}
          , (select isnull(max(ship_seq), 0) + 1
               from ships_contract
              where contract_no = #{contractNo})
          , #{shipName}
          , #{imoNo}
          , #{equipLent}
          , #{applyStartDate}
          , #{applyFinishDate}
          , 'N'
          , convert(varchar(10), getdate(), 121)
          , #{registUser}
);

mapper.update:
update ships_contract
   set ship_name = #{shipName}
     , imo_no = #{imoNo}
     , equip_lent = #{equipLent}
     , apply_start_date = #{applyStartDate}
     , apply_finish_date = #{applyFinishDate}
     , modify_date = convert(varchar(10), getdate(), 121)
     , modify_user = #{modifyUser}
 where contract_no = #{contractNo}
   and ship_seq = #{shipSeq};

mapper.delete:
delete from ships_contract
 where contract_no = #{contractNo}
   and ship_seq = #{shipSeq};

mapper.insert:
insert into apply_plan (
            contract_no
          , ship_seq
          , plan_seq
          , plan_id
          , apply_start_date
          , apply_finish_date
          , regist_date
          , regist_user
)
values (
            #{contractNo}
          , #{shipSeq}
          , (select isnull(max(ship_seq), 0) + 1
               from apply_plan
              where contract_no = #{contractNo}
                and ship_seq = #{shipSeq})
          , #{planId}
          , #{applyStartDate}
          , #{applyFinishDate}
          , convert(varchar(10), getdate(), 121)
          , #{registUser}
);

update apply_plan 
   set plan_id = #{planId}
     , apply_start_date = #{applyStartDate}
     , apply_finish_date = #{applyFinishDate}
     , modify_date = convert(varchar(10), getdate(), 121)
     , modify_user = #{modifyUser}
 where contract_no = #{contractNo}
   and ship_seq = #{shipSeq}
   and plan_seq = #{planSeq};

delete from apply_plan 
 where contract_no = #{contractNo}
   and ship_seq = #{shipSeq}
   and plan_seq = #{planSeq};
