mapper.insert:
insert into contract (
            company_no
          , contract_no
          , contract_name
          , contract_date
          , contractor
          , contract_period
          , start_date
          , end_date
          , monetary_unit
          , exchange_rate
          , remark
          , regist_datetime
          , regist_company
          , regist_user
)
values (
          , #{companyNo}
          , (select 'C' + convert(varchar(8), convert(datetime, #{contractDate}), 12) + format(isnull(max(company_no), 0) + 1, '000') 
               from contract
              where company_no = #{companyNo}
                and contract_date = #{contractDate})
          , #{contractName}
          , #{contractDate}
          , #{contractor}
          , #{contractPeriod}
          , #{startDate}
          , #{endDate}
          , #{monetaryUnit}
          , #{exchangeRate}
          , #{remark}
          , convert(varchar(20), getdate(), 20)
          , #{registCompany}
          , #{registUser}
);

mapper.update:
update contract
   set contract_name = #{contractName}
     , contract_date = #{contractDate}
     , contractor = #{contractor}
     , contract_period = #{contractPeriod}
     , start_date = #{startDate}
     , end_date = #{endDate}
     , monetary_unit = #{monetaryUnit}
     , exchange_rate = #{exchangeRate}
     , remark = #{remark}
     , modify_datetime = convert(varchar(20), getdate(), 20)
     , modify_company = #{modifyCompany}
     , modify_user = #{modifyUser}
 where company_no = #{companyNo}
   and contract_no = #{contractNo};

mapper.delete:
delete from contract
 where company_no = #{companyNo}
   and contract_no = #{contractNo};
