mapper.select:
select count(1) as cnt
  from vw_dashboard_user
 where user_id = #{email};

mapper.insert:
insert into contract_manager (
            contract_no
          , seq
          , email
          , pwd
          , name
          , user_auth
          , use_yn
          , regist_date
)
values (
            #{contractNo}
          , (select isnull(max(seq), 0) + 1
               from contract_manager
              where contract_no = #{contractNo})
          , #{email}
          , convert(varbinary(MAX), #{pwd})
          , #{name}
          , #{userAuth}
          , #{useYn}
          , convert(varchar(10), getdate(), 121)
);

mapper.update:
update contract_manager
   set email = #{email}
     , name = #{name}
     , user_auth = #{userAuth}
     , use_yn = #{useYn}
     , modify_date = convert(varchar(10), getdate(), 121)
 where contract_no = #{contractNo}
   and seq = #{seq};

mapper.delete:
delete from contract_manager
 where contract_no = #{contractNo}
   and seq = #{seq};

mapper.update:
update contract_manager
   set pwd = convert(varbinary(MAX), #{pwd})
     , modify_date = convert(varchar(10), getdate(), 121)
 where contract_no = #{contractNo}
   and seq = #{seq};
