mapper.insert:
insert into employee (
            company_no
          , emp_no
          , emp_name
          , emp_ename
          , email
          , enter_date
          , nation
          , id_no
          , quit_date
          , account_no
)
values (
            #{companyNo}
          , #{empNo}
          , #{empName}
          , #{empEname}
          , #{email}
          , #{enterDate}
          , #{nation}
          , #{idNo}
          , #{quitDate}
          , select isnull(max(account_no), 1000000) + 1 from employee 
);

mapper.update:
update employee
   set emp_name = #{empName}
     , emp_ename = #{empEname}
     , email = #{email}
     , enter_date = #{enterDate}
     , nation = #{nation}
     , id_no = #{idNo}
     , quit_date = #{quitDate}
 where emp_no = #{empNo};

update employee
   set password = convert(varbinary(MAX), #{password})
 where emp_no = #{empNo};
