mapper.insert:
insert into license (
            company_no
          , contract_no
          , license_no
          , license_type
          , app_name
          , license_count
          , start_date
          , end_date
          , unit_price
          , amount
          , discounted_amount
          , actual_amount
          , regist_datetime
          , regist_company
          , regist_user
)
values (
            #{companyNo}
          , #{contractNo}
          , (select #{licenseType} + format(isnull(substring(max(license_no), 3, 2), 0) + 1, '00') + #{appName}
               from license
              where company_no = #{companyNo}
                and contract_no = #{contractNo}
                and license_type = #{licenseType})
          , #{licenseType}
          , #{appName}
          , #{licenseCount}
          , #{startDate}
          , #{endDate}
          , #{unitPrice}
          , #{amount}
          , #{discountedAmount}
          , #{actualAmount}
          , convert(varchar(20), getdate(), 20)
          , #{registCompany}
          , #{registUser}
);

mapper.update:
update license
   set license_count = #{licenseCount}
     , start_date = #{startDate}
     , end_date = #{endDate}
     , unit_price = #{unitPrice}
     , amount = #{amount}
     , discounted_amount = #{discountedAmount}
     , actual_amount = #{actualAmount}
     , modify_datetime = convert(varchar(20), getdate(), 20)
     , modify_company = #{modifyCompany}
     , modify_user = #{modifyUser}
 where company_no = #{companyNo}
   and contract_no = #{contractNo}
   and license_no = #{licenseNo};

mapper.delete:
delete from license
 where company_no = #{companyNo}
   and contract_no = #{contractNo}
   and license_no = #{licenseNo};

delete from license
 where company_no = #{companyNo}
   and contract_no = #{contractNo};
