mapper.insert:
insert into member (
            company_id
          , member_id
          , member_name
          , email
          , member_type
          , phone
          , role_code
          , use_status
          , reg_date
          , reg_company
          , reg_member
)
values (
            #{companyId}
          , (select isnull(max(member_id), 0) + 1
               from member 
              where company_id = #{companyId})  
          , #{memberName}
          , #{email}
          , #{memberType}
          , #{phone}
          , #{roleCode}
          , #{useStatus}
          , convert(varchar(20), getdate(), 20)
          , #{regCompany}
          , #{regMember}
);

mapper.update:
update member
   set member_name = #{memberName}
     , email = #{email}
     , member_type = #{memberType}
     , phone = #{phone}
     , role_code = #{roleCode}
     , use_status = #{useStatus}
     , upt_date = convert(varchar(20), getdate(), 20)
     , upt_company = #{uptCompany}
     , upt_user = #{uptMember}
 where company_id = #{companyId}
   and member_id = #{memberId};

mapper.delete:
delete from member
 where company_id = #{companyId}
   and member_id = #{memberId};
