mapper.insert:
insert into member (
            company_no
          , account_no
          , user_name
          , email
          , user_type
          , nation_code
          , phone_number
          , use_status
          , reg_date
          , reg_company
          , reg_member
)
values (
            #{companyNo}
          , (select isnull(max(account_no), 0) + 1
               from member 
              where company_no = #{companyNo})  
          , #{userName}
          , #{email}
          , #{userType}
          , #{nationCode}
          , #{phoneNumber}
          , #{useStatus}
          , convert(varchar(20), getdate(), 20)
          , #{regCompany}
          , #{regMember}
);

mapper.update:
update member
   set user_name = #{userName}
     , email = #{email}
     , user_type = #{userType}
     , nation_code = #{nationCode}
     , phone_number = #{phoneNumber}
     , use_status = #{useStatus}
     , upt_date = convert(varchar(20), getdate(), 20)
     , upt_company = #{uptCompany}
     , upt_user = #{uptMember}
 where company_no = #{companyNo}
   and account_no = #{accountNo};

mapper.delete:
delete from member
 where company_no = #{companyNo}
   and account_no = #{accountNo};
