mapper.insert:
insert into member (
            company_no
          , account_no
          , user_name
          , email
          , password
          , user_type
          , nation_code
          , phone_number
          , use_status
          , regist_datetime
          , regist_company
          , regist_user
)
values (
            #{companyNo}
          , (select isnull(max(account_no), 0) + 1
               from member 
              where company_no = #{companyNo})  
          , #{userName}
          , #{email}
          , convert(varbinary(MAX), #{password})
          , #{userType}
          , #{nationCode}
          , #{phoneNumber}
          , #{useStatus}
          , convert(varchar(20), getdate(), 20)
          , #{registCompany}
          , #{registUser}
);

mapper.update:
update member
   set user_name = #{userName}
     , email = #{email}
     , user_type = #{userType}
     , nation_code = #{nationCode}
     , phone_number = #{phoneNumber}
     , use_status = #{useStatus}
     , modify_datetime = convert(varchar(20), getdate(), 20)
     , modify_company = #{modifyCompany}
     , modify_user = #{modifyUser}
 where company_no = #{companyNo}
   and account_no = #{accountNo};

mapper.delete:
delete from member
 where company_no = #{companyNo}
   and account_no = #{accountNo};
