mapper.insert:
insert into ts_plan (
            plan_id
          , plan_kname
          , plan_ename
          , flat_div
          , odm_div
          , apply_date
          , finish_date
          , active_yn
          , remark
          , regist_date
          , regist_user
)
values (
            (select isnull(max(plan_id), 0) + 1 from ts_plan)
          , #{planKname}
          , #{planEname}
          , #{flatDiv}
          , #{odmDiv}
          , #{applyDate}
          , #{finishDate}
          , #{activeYn}
          , #{remark}
          , getdate()
          , #{registUser}
);

mapper.update:
update ts_plan
   set plan_kname = #{planKname}
     , plan_ename = #{planEname}
     , flat_div = #{flatDiv}
     , odm_div = #{odmDiv}
     , apply_date = #{applyDate}
     , finish_date = #{finishDate}
     , active_yn = #{activeYn}
     , remark = #{remark}
     , modify_date = getdate()
     , modify_user = #{modifyUser}
 where plan_id = #{planId};

mapper.insert:
insert into ts_plan_amount (
            plan_id
          , plan_seq
          , amount
          , time
          , bill_base_1
          , surcharge
          , bill_base_2
          , apply_date
          , finish_date
          , active_yn
          , regist_date
          , regist_user
)
values (
            #{planId}
          , (select isnull(max(plan_seq), 0) + 1 
               from ts_plan_amount
              where plan_id = #{planId})
          , #{amount}
          , #{time}
          , #{billBase1}
          , #{surcharge}
          , #{billBase2}
          , #{applyDate}
          , #{finishDate}
          , #{activeYn}
          , getdate()
          , #{registUser}
);

mapper.update:
update ts_plan_amount
   set amount = #{amount}
     , time = #{time}
     , bill_base_1 = #{billBase1}
     , surcharge = #{surcharge}
     , bill_base_2 = #{billBase2}
     , apply_date = #{applyDate}
     , finish_date = #{finishDate}
     , active_yn = #{activeYn}
     , modify_date = getdate()
     , modify_user = #{modifyUser}
 where plan_id = #{planId}
   and plan_seq = #{planSeq}

mapper.delete:
delete from ts_plan_amount
 where plan_id = #{planId}
   and plan_seq = #{planSeq};
